// client/src/AdminDashboard.js
// IMPORTANT: To resolve "Could not resolve" errors for FullCalendar,
// ensure you have installed all FullCalendar packages in your client directory:
// npm install @fullcalendar/react @fullcalendar/daygrid @fullcalendar/timegrid @fullcalendar/list
// After installation, restart your frontend development server (npm start).

import React, { useState, useEffect } from 'react';
import axios from 'axios';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import listPlugin from '@fullcalendar/list';
import FullCalendar from '@fullcalendar/react';

// Modal for Add/Edit Session
function SessionModal({ isOpen, onClose, onSave, sessionData, trainers }) {
    const [formData, setFormData] = useState(sessionData || {
        trainer_id: '',
        course_name: '',
        date: '',
        time: '',
        location: ''
    });
    const [formError, setFormError] = useState('');

    useEffect(() => {
        // Update form data if sessionData changes (for edit mode)
        if (sessionData) {
            setFormData(sessionData);
        } else {
            // Reset for add mode
            setFormData({
                trainer_id: '',
                course_name: '',
                date: '',
                time: '',
                location: ''
            });
        }
        setFormError(''); // Clear errors on modal open/data change
    }, [sessionData, isOpen]);

    if (!isOpen) return null;

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prev => ({ ...prev, [name]: value }));
    };

    const handleSubmit = (e) => {
        e.preventDefault();
        setFormError('');
        // Basic validation
        if (!formData.trainer_id || !formData.course_name || !formData.date || !formData.time || !formData.location) {
            setFormError('All fields are required.');
            return;
        }
        onSave(formData);
    };

    return (
        <div className="fixed inset-0 bg-gray-600 bg-opacity-50 flex items-center justify-center p-4 z-50">
            <div className="bg-white rounded-lg shadow-xl p-6 w-full max-w-md">
                <h2 className="text-2xl font-bold text-gray-800 mb-4">{sessionData ? 'Edit Session' : 'Add Session'}</h2>
                {formError && <p className="text-red-600 text-sm mb-4">{formError}</p>}
                <form onSubmit={handleSubmit} className="space-y-4">
                    <div>
                        <label htmlFor="trainer_id" className="block text-sm font-medium text-gray-700">Trainer</label>
                        <select
                            id="trainer_id"
                            name="trainer_id"
                            value={formData.trainer_id}
                            onChange={handleChange}
                            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                            required
                        >
                            <option value="">Select Trainer</option>
                            {trainers.map(trainer => (
                                <option key={trainer.id} value={trainer.id}>{trainer.email}</option>
                            ))}
                        </select>
                    </div>
                    <div>
                        <label htmlFor="course_name" className="block text-sm font-medium text-gray-700">Course Name</label>
                        <input
                            type="text"
                            id="course_name"
                            name="course_name"
                            value={formData.course_name}
                            onChange={handleChange}
                            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                            required
                        />
                    </div>
                    <div>
                        <label htmlFor="date" className="block text-sm font-medium text-gray-700">Date</label>
                        <input
                            type="date"
                            id="date"
                            name="date"
                            value={formData.date}
                            onChange={handleChange}
                            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                            required
                        />
                    </div>
                    <div>
                        <label htmlFor="time" className="block text-sm font-medium text-gray-700">Time</label>
                        <input
                            type="time"
                            id="time"
                            name="time"
                            value={formData.time}
                            onChange={handleChange}
                            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                            required
                        />
                    </div>
                    <div>
                        <label htmlFor="location" className="block text-sm font-medium text-gray-700">Location</label>
                        <input
                            type="text"
                            id="location"
                            name="location"
                            value={formData.location}
                            onChange={handleChange}
                            className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-indigo-500 focus:border-indigo-500 sm:text-sm"
                            required
                        />
                    </div>
                    <div className="flex justify-end space-x-3 mt-6">
                        <button
                            type="button"
                            onClick={onClose}
                            className="px-4 py-2 bg-gray-300 text-gray-800 rounded-md hover:bg-gray-400 transition duration-200"
                        >
                            Cancel
                        </button>
                        <button
                            type="submit"
                            className="px-4 py-2 bg-indigo-600 text-white rounded-md hover:bg-indigo-700 transition duration-200"
                        >
                            Save Session
                        </button>
                    </div>
                </form>
            </div>
        </div>
    );
}


function AdminDashboard({ user, onLogout }) {
    const [events, setEvents] = useState([]);
    const [trainers, setTrainers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState(null);
    const [notification, setNotification] = useState('');

    const [isModalOpen, setIsModalOpen] = useState(false);
    const [currentSession, setCurrentSession] = useState(null); // For editing

    // Function to fetch all sessions and trainers
    const fetchAllData = async () => {
        setLoading(true);
        setError(null);
        try {
            const token = localStorage.getItem('token');
            if (!token) {
                setError('No authentication token found.');
                setLoading(false);
                return;
            }

            // Fetch sessions
            const sessionsResponse = await axios.get('http://localhost:5000/api/sessions', {
                headers: { Authorization: `Bearer ${token}` },
            });

            // Fetch trainers
            const trainersResponse = await axios.get('http://localhost:5000/api/users/trainers', {
                headers: { Authorization: `Bearer ${token}` },
            });
            setTrainers(trainersResponse.data);

            // Map events for FullCalendar
            const mappedEvents = sessionsResponse.data.map(session => ({
                id: session.id,
                title: `${session.course_name} - ${session.location} (Trainer: ${session.trainer_email || 'N/A'})`,
                date: session.date,
                startTime: session.time,
                extendedProps: {
                    location: session.location,
                    trainerId: session.trainer_id,
                    courseName: session.course_name,
                    trainerEmail: session.trainer_email // For display in modal/details
                },
                color: getColorForTrainer(session.trainer_id), // Assign color based on trainer
                // Highlight today's events
                classNames: new Date(session.date).toDateString() === new Date().toDateString() ? ['bg-purple-500', 'text-white', 'font-bold'] : [],
            }));
            setEvents(mappedEvents);

            // Check for upcoming sessions in the next 24 hours
            const now = new Date();
            const next24Hours = new Date(now.getTime() + 24 * 60 * 60 * 1000);
            const upcoming = sessionsResponse.data.filter(session => {
                const sessionDateTime = new Date(`${session.date}T${session.time}`);
                return sessionDateTime > now && sessionDateTime <= next24Hours;
            });

            if (upcoming.length > 0) {
                const upcomingTitles = upcoming.map(s => `${s.course_name} at ${s.time} by ${s.trainer_email}`).join(', ');
                setNotification(`Upcoming sessions in next 24 hrs: ${upcomingTitles}`);
            } else {
                setNotification('No upcoming sessions in the next 24 hours.');
            }

        } catch (err) {
            console.error('Error fetching data for admin dashboard:', err);
            setError('Failed to load data. Please ensure backend is running and you have admin privileges.');
            setNotification('Failed to load upcoming session notifications.');
        } finally {
            setLoading(false);
        }
    };

    useEffect(() => {
        fetchAllData();
    }, [user]); // Refetch when user changes

    // Simple color assignment based on trainer ID
    const getColorForTrainer = (trainerId) => {
        const colors = ['#3f51b5', '#e91e63', '#009688', '#ff9800', '#673ab7', '#8bc34a']; // Sample colors
        return colors[trainerId % colors.length]; // Cycle through colors
    };

    const handleAddSessionClick = () => {
        setCurrentSession(null); // Clear any existing session data
        setIsModalOpen(true);
    };

    const handleEditSession = (info) => {
        // info.event.extendedProps contains our custom data
        const sessionToEdit = {
            id: info.event.id,
            trainer_id: info.event.extendedProps.trainerId,
            course_name: info.event.extendedProps.courseName,
            date: info.event.startStr.split('T')[0], // Extract YYYY-MM-DD
            time: info.event.extendedProps.startTime,
            location: info.event.extendedProps.location
        };
        setCurrentSession(sessionToEdit);
        setIsModalOpen(true);
    };

    const handleDeleteSession = async (info) => {
        if (window.confirm(`Are you sure you want to delete session "${info.event.title}"?`)) {
            try {
                const token = localStorage.getItem('token');
                await axios.delete(`http://localhost:5000/api/sessions/${info.event.id}`, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                alert('Session deleted successfully!');
                fetchAllData(); // Refresh calendar
            } catch (err) {
                console.error('Error deleting session:', err);
                alert('Failed to delete session. Check console for details.');
            }
        }
    };

    const handleSaveSession = async (data) => {
        try {
            const token = localStorage.getItem('token');
            if (data.id) {
                // Edit existing session
                await axios.put(`http://localhost:5000/api/sessions/${data.id}`, data, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                alert('Session updated successfully!');
            } else {
                // Add new session
                await axios.post('http://localhost:5000/api/sessions', data, {
                    headers: { Authorization: `Bearer ${token}` },
                });
                alert('Session added successfully!');
            }
            setIsModalOpen(false);
            fetchAllData(); // Refresh calendar
        } catch (err) {
            console.error('Error saving session:', err);
            alert('Failed to save session. Check console for details.');
        }
    };

    if (loading) return <div className="min-h-screen flex items-center justify-center bg-gray-100"><p className="text-gray-700 text-lg">Loading data...</p></div>;
    if (error) return <div className="min-h-screen flex items-center justify-center bg-red-100"><p className="text-red-700 text-lg">{error}</p></div>;

    return (
        <div className="min-h-screen bg-gradient-to-br from-purple-500 to-pink-600 text-white p-6 flex flex-col items-center">
            <div className="bg-white text-gray-800 p-8 rounded-xl shadow-lg w-full max-w-4xl text-center mb-8">
                <h2 className="text-4xl font-extrabold mb-4">Welcome, Admin {user.email}!</h2>
                <p className="text-lg mb-4">Your ID: {user.id} | Role: {user.role}</p>
                <button
                    onClick={onLogout}
                    className="bg-red-500 text-white py-2 px-6 rounded-lg hover:bg-red-600 focus:outline-none focus:ring-2 focus:ring-red-400 focus:ring-offset-2 transition duration-200"
                >
                    Logout
                </button>
            </div>

            {notification && (
                <div className="bg-yellow-100 border-l-4 border-yellow-500 text-yellow-700 p-4 rounded-lg shadow-md w-full max-w-4xl mb-6" role="alert">
                    <p className="font-bold">Notification:</p>
                    <p>{notification}</p>
                </div>
            )}

            <div className="bg-white p-8 rounded-xl shadow-lg w-full max-w-4xl">
                <h3 className="text-2xl font-bold text-gray-800 mb-4">All Trainer Timetables</h3>
                <div className="mb-4 flex justify-end">
                    <button
                        onClick={handleAddSessionClick}
                        className="bg-indigo-600 text-white py-2 px-4 rounded-lg hover:bg-indigo-700 focus:outline-none focus:ring-2 focus:ring-indigo-500 focus:ring-offset-2 transition duration-200"
                    >
                        Add New Session
                    </button>
                </div>
                <FullCalendar
                    plugins={[dayGridPlugin, timeGridPlugin, listPlugin]}
                    initialView="dayGridMonth"
                    events={events}
                    headerToolbar={{
                        left: 'prev,next today',
                        center: 'title',
                        right: 'dayGridMonth,timeGridWeek,timeGridDay,listWeek'
                    }}
                    editable={true} // Allow dragging and resizing events
                    selectable={true} // Allow selecting dates for new events
                    eventClick={handleEditSession} // Handle click for edit/delete
                    eventDrop={handleEditSession} // Handle event drag-and-drop (updates date/time)
                    eventResize={handleEditSession} // Handle event resizing (updates duration)
                    height="auto"
                />
            </div>

            {/* Session Add/Edit Modal */}
            <SessionModal
                isOpen={isModalOpen}
                onClose={() => setIsModalOpen(false)}
                onSave={handleSaveSession}
                sessionData={currentSession}
                trainers={trainers}
            />
        </div>
    );
}

export default AdminDashboard;

// client/src/AdminDashboard.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import dayGridPlugin from '@fullcalendar/daygrid';
import timeGridPlugin from '@fullcalendar/timegrid';
import listPlugin from '@fullcalendar/list';
import FullCalendar from '@fullcalendar/react';
import { saveAs } from 'file-saver';
import jsPDF from 'jspdf';
import 'jspdf-autotable';
import ReactPaginate from 'react-paginate';
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import Papa from 'papaparse';
import TrainerUtilization from './TrainerUtilization';
import Modal from 'react-modal';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import moment from 'moment';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import * as XLSX from 'xlsx';

import './styles/calendarStyles.css';
import './styles/dashboardStyles.css';

function SessionModal({ isOpen, onClose, onSave, sessionData, trainers }) {
  const [formData, setFormData] = useState(sessionData || {
    trainer_id: '', course_name: '', date: '', time: '', location: '', duration: '', attended: false, emailNotify: true
  });

  useEffect(() => {
    if (sessionData) {
      setFormData(sessionData);
    } else {
      setFormData({ trainer_id: '', course_name: '', date: '', time: '', location: '', duration: '', attended: false, emailNotify: true });
    }
  }, [sessionData, isOpen]);

  if (!isOpen) return null;

  const handleChange = e => {
    const { name, value, type, checked } = e.target;
    setFormData(prev => ({ ...prev, [name]: type === 'checkbox' ? checked : value }));
  };

  const handleSubmit = e => {
    e.preventDefault();
    if (!formData.duration || isNaN(formData.duration) || formData.duration <= 0) {
      toast.error('Please enter a valid session duration.');
      return;
    }
    onSave(formData);
  };

  return (
    <div className="fixed inset-0 bg-black bg-opacity-40 flex items-center justify-center z-50">
      <div className="bg-white p-6 rounded shadow-lg w-full max-w-md">
        <h2 className="text-xl font-bold mb-4">{sessionData ? 'Edit Session' : 'Add Session'}</h2>
        <form onSubmit={handleSubmit} className="space-y-3">
          <select name="trainer_id" value={formData.trainer_id} onChange={handleChange} className="w-full border p-2 rounded">
            <option value="">Select Trainer</option>
            {trainers.map(t => <option key={t.id} value={t.id}>{t.email}</option>)}
          </select>
          <input name="course_name" value={formData.course_name} onChange={handleChange} placeholder="Course Name" className="w-full border p-2 rounded" />
          <input type="date" name="date" value={formData.date} onChange={handleChange} className="w-full border p-2 rounded" />
          <input type="time" name="time" value={formData.time} onChange={handleChange} className="w-full border p-2 rounded" />
          <input name="location" value={formData.location} onChange={handleChange} placeholder="Location" className="w-full border p-2 rounded" />
          <input name="duration" type="number" min="1" placeholder="Duration (minutes)" value={formData.duration} onChange={handleChange} className="w-full border p-2 rounded" />
          <label className="inline-flex items-center">
            <input type="checkbox" name="attended" checked={formData.attended} onChange={handleChange} className="mr-2" />
            Mark as Attended
          </label>
          <label className="inline-flex items-center">
            <input type="checkbox" name="emailNotify" checked={formData.emailNotify} onChange={handleChange} className="mr-2" />
            Send Email Notification
          </label>
          <div className="flex justify-end gap-2">
            <button type="button" onClick={onClose} className="bg-gray-300 px-4 py-2 rounded">Cancel</button>
            <button type="submit" className="bg-indigo-500 text-white px-4 py-2 rounded">Save</button>
          </div>
        </form>
      </div>
    </div>
  );
}

function UserModal({ isOpen, onClose, onSave, userData }) {
  const [form, setForm] = useState(userData || { name: '', email: '', password: '', role: 'trainer' });
  useEffect(() => { setForm(userData || { name: '', email: '', password: '', role: 'trainer' }); }, [userData, isOpen]);
  if (!isOpen) return null;
  const handleChange = e => { const { name, value } = e.target; setForm(f => ({ ...f, [name]: value })); };
  const handleSubmit = e => { e.preventDefault(); onSave(form); };
  return (
    <Modal isOpen={isOpen} onRequestClose={onClose} ariaHideApp={false} className="modal" overlayClassName="modal-overlay">
      <h2 className="text-xl font-bold mb-4">{userData ? 'Edit User' : 'Add User'}</h2>
      <form onSubmit={handleSubmit} className="space-y-3">
        <input name="name" value={form.name} onChange={handleChange} placeholder="Name" className="w-full border p-2 rounded" required />
        <input name="email" value={form.email} onChange={handleChange} placeholder="Email" className="w-full border p-2 rounded" required type="email" />
        <input name="password" value={form.password} onChange={handleChange} placeholder="Password" className="w-full border p-2 rounded" type="password" minLength={userData ? 0 : 6} />
        <select name="role" value={form.role} onChange={handleChange} className="w-full border p-2 rounded">
          <option value="trainer">Trainer</option>
          <option value="admin">Admin</option>
        </select>
        <div className="flex justify-end gap-2">
          <button type="button" onClick={onClose} className="bg-gray-300 px-4 py-2 rounded">Cancel</button>
          <button type="submit" className="bg-indigo-500 text-white px-4 py-2 rounded">Save</button>
        </div>
      </form>
    </Modal>
  );
}

function sendEmailNotification(session) {
  const token = localStorage.getItem('token');
  axios.post('/api/notifications/email', {
    subject: 'New Session Scheduled',
    message: `You have a new session: ${session.course_name} on ${session.date} at ${session.time}`,
    recipient: session.trainer_email
  }, {
    headers: { Authorization: `Bearer ${token}` }
  }).then(() => {
    toast.success('Email notification sent');
  }).catch(() => {
    toast.error('Failed to send email');
  });
}

function getTodayAndUpcomingSessions(sessions) {
  const today = new Date();
  const todayStr = today.toISOString().slice(0, 10);
  const upcomingLimit = new Date(today);
  upcomingLimit.setDate(today.getDate() + 7);
  const todaySessions = sessions.filter(s => s.date === todayStr);
  const upcomingSessions = sessions.filter(s => {
    const d = new Date(s.date);
    return d > today && d <= upcomingLimit;
  });
  return { todaySessions, upcomingSessions };
}

function AdminDashboard({ user, onLogout }) {
  const [sessions, setSessions] = useState([]);
  const [trainers, setTrainers] = useState([]);
  const [users, setUsers] = useState([]);
  const [loading, setLoading] = useState(true);
  const [modalOpen, setModalOpen] = useState(false);
  const [editingSession, setEditingSession] = useState(null);
  const [currentPage, setCurrentPage] = useState(1);
  const [searchQuery, setSearchQuery] = useState('');
  const [userModalOpen, setUserModalOpen] = useState(false);
  const [editingUser, setEditingUser] = useState(null);
  const itemsPerPage = 10;
  const [calendarEvents, setCalendarEvents] = useState([]);
  const [calendarSource, setCalendarSource] = useState('backend'); // 'backend' or 'excel'
  const [excelFileName, setExcelFileName] = useState('');
  const localizer = momentLocalizer(moment);
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    if (calendarSource === 'backend') {
      setCalendarEvents(sessions.map(session => ({
        id: session.id,
        title: `${session.course_name} (${session.location}) - ${trainers.find(t => t.id === session.trainer_id)?.email || 'Unknown'}`,
        start: new Date(`${session.date}T${session.time}`),
        end: new Date(new Date(`${session.date}T${session.time}`).getTime() + ((session.duration || 60) * 60000)),
        allDay: false,
        resource: session
      })));
    }
  }, [sessions, trainers, calendarSource]);

  // Fetch notifications for admin
  useEffect(() => {
    if (user.role === 'admin') {
      const fetchNotifications = async () => {
        try {
          const token = localStorage.getItem('token');
          const res = await axios.get('/api/notifications', { headers: { Authorization: `Bearer ${token}` } });
          setNotifications(res.data);
        } catch (err) {
          // ignore
        }
      };
      fetchNotifications();
    }
  }, [user]);

  const handleExcelUpload = (e) => {
    const file = e.target.files[0];
    if (!file) return;
    setExcelFileName(file.name);
    const reader = new FileReader();
    reader.onload = (evt) => {
      const data = new Uint8Array(evt.target.result);
      const workbook = XLSX.read(data, { type: 'array' });
      const sheetName = workbook.SheetNames[0];
      const worksheet = workbook.Sheets[sheetName];
      const json = XLSX.utils.sheet_to_json(worksheet, { header: 1 });
      // Assume first row is header
      const header = json[0];
      const rows = json.slice(1);
      // Map Excel columns to event fields
      const events = rows.map((row, idx) => {
        const get = (col) => row[header.indexOf(col)] || '';
        const date = get('Date') || get('Session Date') || get('session_date') || get('date');
        const time = get('Time') || get('Session Time') || get('session_time') || get('time') || '09:00';
        const duration = parseInt(get('Duration') || 60);
        return {
          id: idx + 10000, // avoid collision with backend ids
          title: `${get('Course') || get('Course Name') || get('course_name')} (${get('Location') || get('location')}) - ${get('Trainer') || get('trainer_email')}`,
          start: new Date(`${date}T${time}`),
          end: new Date(new Date(`${date}T${time}`).getTime() + (duration * 60000)),
          allDay: false,
          resource: row
        };
      });
      setCalendarEvents(events);
      setCalendarSource('excel');
    };
    reader.readAsArrayBuffer(file);
  };

  const handleCalendarSourceChange = (src) => {
    setCalendarSource(src);
    if (src === 'backend') setExcelFileName('');
  };

  const fetchData = async () => {
    try {
      const token = localStorage.getItem('token');
      const [sessionsRes, trainersRes, usersRes] = await Promise.all([
        axios.get('/api/sessions', { headers: { Authorization: `Bearer ${token}` } }),
        axios.get('/api/users', { headers: { Authorization: `Bearer ${token}` } }),
        axios.get('/api/users', { headers: { Authorization: `Bearer ${token}` } })
      ]);
      setSessions(sessionsRes.data);
      setTrainers(trainersRes.data.filter(t => t.role === 'trainer'));
      setUsers(usersRes.data);
    } catch (error) {
      console.error('Error fetching data:', error);
      toast.error('Failed to load data');
    } finally {
      setLoading(false);
    }
  };

  const handleSaveSession = async (sessionData) => {
    try {
      const token = localStorage.getItem('token');
      if (editingSession) {
        await axios.put(`/api/sessions/${editingSession.id}`, sessionData, {
          headers: { Authorization: `Bearer ${token}` }
        });
        toast.success('Session updated successfully');
      } else {
        await axios.post('/api/sessions', sessionData, {
          headers: { Authorization: `Bearer ${token}` }
        });
        toast.success('Session created successfully');
      }
      setModalOpen(false);
      setEditingSession(null);
      fetchData();
    } catch (error) {
      console.error('Error saving session:', error);
      toast.error('Failed to save session');
    }
  };

  const handleDeleteSession = async (sessionId) => {
    if (!window.confirm('Are you sure you want to delete this session?')) return;

    try {
      const token = localStorage.getItem('token');
      await axios.delete(`/api/sessions/${sessionId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      toast.success('Session deleted successfully');
      fetchData();
    } catch (error) {
      console.error('Error deleting session:', error);
      toast.error('Failed to delete session');
    }
  };

  const exportCSV = () => {
    const csv = Papa.unparse(sessions.map(s => ({
      Course: s.course_name,
      Date: s.date,
      Time: s.time,
      Location: s.location,
      Trainer: trainers.find(t => t.id === s.trainer_id)?.email || 'Unknown',
      Attended: s.attended ? 'Yes' : 'No'
    })));
    const blob = new Blob([csv], { type: 'text/csv;charset=utf-8;' });
    saveAs(blob, 'admin_sessions.csv');
  };

  const exportPDF = () => {
    const doc = new jsPDF();
    doc.text('Admin Sessions Report', 14, 16);
    doc.autoTable({
      startY: 20,
      head: [['Course', 'Date', 'Time', 'Location', 'Trainer', 'Attended']],
      body: sessions.map(s => [
        s.course_name,
        s.date,
        s.time,
        s.location,
        trainers.find(t => t.id === s.trainer_id)?.email || 'Unknown',
        s.attended ? 'Yes' : 'No'
