    });
    doc.save('admin_sessions.pdf');
  };

  const handleSaveUser = async (form) => {
    try {
      const token = localStorage.getItem('token');
      if (editingUser) {
        await axios.put(`/api/users/${editingUser.id}`, form, { headers: { Authorization: `Bearer ${token}` } });
