        {/* Notifications */}
        {notifications.length > 0 && (
          <div className="dashboard-card" style={{ background: 'rgba(236,239,255,0.7)', marginBottom: 16 }}>
            <h3 className="dashboard-section-title">Trainer Activity Notifications</h3>
            <ul>
