// client/src/CalendarView.js
import React, { useEffect, useState } from 'react';
import { Calendar, momentLocalizer } from 'react-big-calendar';
import { format, parseISO } from 'date-fns';
import 'react-big-calendar/lib/css/react-big-calendar.css';
import { parseISO as parseISODate } from 'date-fns';
import { zonedTimeToUtc } from 'date-fns-tz';

import moment from 'moment';
const localizer = momentLocalizer(moment);

function CalendarView({ token }) {
    const [events, setEvents] = useState([]);

    useEffect(() => {
        async function fetchSessions() {
            try {
                const res = await fetch('http://localhost:5000/api/sessions', {
                    headers: {
                        'Authorization': `Bearer ${token}`
                    }
                });

                const data = await res.json();
                const formattedEvents = data.map(session => {
                    const start = new Date(`${session.session_date}T${session.session_time}`);
                    const end = new Date(start.getTime() + 60 * 60 * 1000); // 1 hour duration
                    return {
                        id: session.id,
                        title: `${session.course_name} (${session.location})`,
                        start,
                        end,
                        allDay: false,
                        resource: session
                    };
                });

                setEvents(formattedEvents);
            } catch (err) {
                console.error('Error loading sessions:', err);
            }
        }

        fetchSessions();
    }, [token]);

    // Check if event is today or upcoming
    const isToday = (date) => {
        const today = new Date();
        return date.toDateString() === today.toDateString();
    };

    return (
        <div className="p-4">
            <h2 className="text-2xl font-bold mb-4">Trainer Schedule</h2>
            <Calendar
                localizer={localizer}
                events={events}
                startAccessor="start"
                endAccessor="end"
                style={{ height: 600 }}
                views={['month', 'week', 'day']}
                tooltipAccessor={(event) => `${event.title} - ${format(event.start, 'PPpp')}`}
                eventPropGetter={(event) => {
                    const style = {
                        backgroundColor: isToday(event.start) ? '#34D399' : '#3B82F6',
                        color: 'white',
                        borderRadius: '6px',
                        padding: '4px'
                    };
                    return { style };
                }}
            />
        </div>
    );
}

export default CalendarView;

// src/components/Login.js
import React, { useState } from 'react';
import './App.css';

function Login({ onLogin, onGoToRegister }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [loading, setLoading] = useState(false);

  const handleLogin = async () => {
    if (!email || !password) {
      alert('Please fill in all fields');
      return;
    }

    setLoading(true);
    try {
      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({ error: 'Network error' }));
        throw new Error(errorData.error || `HTTP ${response.status}`);
      }

      const data = await response.json();
      alert('Login success');

      // Save token and notify App
      localStorage.setItem('token', data.token);
      if (onLogin) {
        onLogin(data.token);
      }
    } catch (err) {
      alert(`Login failed: ${err.message}`);
      console.error('Login error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="login-form">
        <h2 className="login-title">Trainer Login</h2>
        <div className="form-group">
          <input
            type="email"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            disabled={loading}
            className="form-input"
          />
        </div>
        <div className="form-group">
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            disabled={loading}
            className="form-input"
          />
        </div>
        <button 
          onClick={handleLogin} 
          disabled={loading}
          className="login-button"
        >
          {loading ? 'Logging in...' : 'Login'}
        </button>
        <p className="register-link">
          Don't have an account? 
          <button 
            onClick={onGoToRegister}
            className="register-button"
          >
            Register
          </button>
        </p>
      </div>
    </div>
  );
}

export default Login;

// src/components/Register.js
import React, { useState } from 'react';
import './App.css';

function Register({ onRegisterSuccess, onGoToLogin }) {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [role, setRole] = useState('trainer');
  const [loading, setLoading] = useState(false);
  const [name, setName] = useState('');

  const handleRegister = async () => {
    if (!email || !password || !name) {
      alert('Please fill in all fields');
      return;
    }

    if (password.length < 6) {
      alert('Password must be at least 6 characters long');
      return;
    }

    setLoading(true);
    try {
      const response = await fetch('/api/auth/register', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ name, email, password, role }),
      });

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({ error: 'Network error' }));
        throw new Error(errorData.error || `HTTP ${response.status}`);
      }

      const data = await response.json();
      alert(data.message);
      
      // Clear form on success
      setEmail('');
      setPassword('');
      setRole('trainer');
      setName('');
      
      // Notify parent component
      if (onRegisterSuccess) {
        onRegisterSuccess();
      }
    } catch (err) {
      alert(`Registration failed: ${err.message}`);
      console.error('Registration error:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="register-container">
      <div className="register-form">
        <h2 className="register-title">Register</h2>
        <div className="form-group">
          <input 
            type="text" 
            placeholder="Name" 
            value={name}
            onChange={(e) => setName(e.target.value)}
            disabled={loading}
            className="form-input"
          />
        </div>
        <div className="form-group">
          <input 
            type="email" 
            placeholder="Email" 
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            disabled={loading}
            className="form-input"
          />
        </div>
        <div className="form-group">
          <input 
            type="password" 
            placeholder="Password" 
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            disabled={loading}
            className="form-input"
          />
        </div>
        <div className="form-group">
          <select 
            onChange={(e) => setRole(e.target.value)} 
            value={role}
            disabled={loading}
            className="form-select"
          >
            <option value="trainer">Trainer</option>
            <option value="admin">Admin</option>
          </select>
        </div>
        <button onClick={handleRegister} disabled={loading} className="register-button">
          {loading ? 'Registering...' : 'Register'}
        </button>
        <p className="register-link">
          Already have an account? 
          <button 
            onClick={onGoToLogin}
            className="login-button"
          >
            Login
          </button>
        </p>
      </div>
    </div>
  );
}

export default Register;

